import{E as o}from"./EndedPageInfo.51ca7467.js";import{d as i,_ as n,r,o as l,c,a as e,b as u,p as d,e as p}from"./index.2a9f5442.js";const x=i({name:"Projects",components:{EndedPageInfo:o}}),E="/assets/silene-illu--1.f25a3627.jpg";const t=s=>(d("data-v-6a9e3fbb"),s=s(),p(),s),m={class:"v-projects s-container"},_=t(()=>e("img",{class:"s-page-header",alt:"Sil\xE8ne logo",src:E},null,-1)),v=t(()=>e("h1",null,"actualit\xE9 / projets",-1)),f={class:"s-page_content"},b=t(()=>e("h2",null,"Les Semailles",-1)),g=t(()=>e("p",null,"Fin 2020, les coop\xE9ratives Cigu\xEB et Sil\xE8ne ont obtenu ensemble un droit de superficie sur une parcelle du quartier des Semailles, au Grand-Lancy. Une nouvelle coop\xE9rative devra \xEAtre fond\xE9e ainsi pour r\xE9unir les deux entit\xE9s. La FPLC, propri\xE9taire du terrain, est l'un des pilotes du projet, avec la ville de Lancy et le canton de Gen\xE8ve. \xC0 cette fin, les investisseurs participent \xE0 l'organisation d'un concours d'urbanisme et d'architecture \xE0 deux degr\xE9s, qui vise \xE0 explorer de multiples sc\xE9narios et \xE0 choisir un projet exemplaire de qualit\xE9. En parall\xE8le, les coop\xE9ratives Cigu\xEB et Sil\xE8ne organisent depuis d\xE9but 2021 des ateliers de concertation pour imaginer un habitat convivial, \xE9cologique et sans voiture. Les ambitions des coop\xE9ratives sont grandes en termes d\u2019\xE9cologie et de mobilit\xE9 ainsi qu\u2019au sujet de la forme urbaine et de la qualit\xE9 des espaces publics, tout en mettant en avant une d\xE9marche participative exemplaire.",-1)),h=t(()=>e("p",null,"Les coop\xE9ratives Cigu\xEB et Sil\xE8ne, ainsi que la Fondation Immobili\xE8re de Droit Public Camille Martin (FCM), avec laquelle elles se sont associ\xE9es pour ce projet, souhaitent d\xE9fendre des hauts standards de qualit\xE9 et porter la voix des coop\xE9rateurices dans ce processus. Leur objectif est de cr\xE9er un b\xE2timent avec une forte mixit\xE9 sociale et programmatique, m\xE9langeant des appartements HBM, des appartements communautaires pour les \xE9tudiants et les post-\xE9tudiants, ainsi que des appartements pour personnes en formation avec enfants. Des espaces partag\xE9s au sein de l\u2019immeuble sont souhait\xE9s pour l\u2019ensemble des futur.x.e.s habitant.x.e.s. Les coop\xE9ratives pr\xE9voient \xE9galement la construction d'espaces communs et d'arcades ouvertes sur le quartier et souhaitent vivement participer \xE0 la vie du voisinage des Semailles.",-1)),j=t(()=>e("p",null,"La coop\xE9rative Sil\xE8ne se r\xE9jouit de cette premi\xE8re exp\xE9rience collaborative et attend avec joie et ferveur la r\xE9alisation de son premier immeuble, dans le quartier des Semailles !",-1));function q(s,S,L,C,B,I){const a=r("ended-page-info");return l(),c("div",m,[_,v,e("div",f,[b,g,h,j,u(a)])])}const F=n(x,[["render",q],["__scopeId","data-v-6a9e3fbb"]]);export{F as default};
